      out.write("\t    console.log('Table Click Event',data);\r\n");
      out.write("\t   \t\r\n");
      out.write("\t\tif($('#REQUEST_TYPE').val()!='2'){ //\"구분-등록\" 상태면 반응 없음.\r\n");
      out.write("\t        $('#BZWR_INDC_CHGR').text(data.BZWR_INDC_CHGR_TXT); //지시자\r\n");
