      out.write("\t        if($('#REQUEST_TYPE').val()=='1' || data.BZWR_STS=='02'){\r\n");
      out.write("\t        \t$('#BZWR_EXC_CHGR_BTN').hide();\r\n");
      out.write("\t\t\t\t$('#BZWR_STS_2').attr(\"disabled\",true);\r\n");
      out.write("\t\t\t\t$('#BZWR_TTL').attr(\"disabled\",true);\r\n");
      out.write("\t\t\t\t$('#BZWR_CNTN').attr(\"disabled\",true);\r\n");
      out.write("\t        }else{\r\n");
      out.write("\t        \t$('#BZWR_EXC_CHGR_BTN').show();\r\n");
      out.write("\t\t\t\t$('#BZWR_STS_2').removeAttr(\"disabled\");\r\n");
      out.write("\t\t\t\t$('#BZWR_TTL').removeAttr(\"disabled\");\r\n");
      out.write("\t\t\t\t$('#BZWR_CNTN').removeAttr(\"disabled\");\r\n");
      out.write("\t        }\r\n");
      out.write("\t\t}\r\n");
      out.write("\t} );\r\n");
      out.write("\r\n");
      out.write("\t/* 선택된  row 하이라이트 */\r\n");
      out.write("\t$('#job_list tbody').on('click', 'tr', function() {\r\n");
      out.write("\t\t$('#job_list tbody > tr').css('background-color','#ffffff');\r\n");
